package javaBasico;

public class Ejemplo_If_Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=2;
		switch(i) {
		case (0):
			System.out.print("es 0");
			break;
		case (1):
			System.out.print("es 1");
		break;
		case (2):
			System.out.print("es 2");
		break;
		default:
			System.out.print("es mas que 2");
			
		}

	}

}
